            "tags": "$:/tags/Macro",
            "text": "\define dbadge(subject,status, type:"primary")
<div class="dbadge"><span class="dbadge-subject">$subject$</span><span class="dbadge-status dbadge-$type$ ">$status$</span></div>
\end
"
